import React, { useState, useRef, useCallback, useEffect } from 'react';
import './DiagonalCardSlider.css';

interface CardData {
  id: number;
  src: string;
  alt: string;
}

const DiagonalCardSlider: React.FC = () => {
  const [offset, setOffset] = useState<number>(0);
  const sliderRef = useRef<HTMLDivElement>(null);
  const isDragging = useRef<boolean>(false);
  const startX = useRef<number>(0);
  const startOffset = useRef<number>(0);
  
  // Список изображений (дублируем для бесконечности)
  const originalCards: CardData[] = [
    { id: 1, src: '/imgCard/4182c3a36ae0287e74f45e4bc4b0478087395003.png', alt: 'Card 1' },
    { id: 2, src: '/imgCard/59ba5cf45380257ee8e166783aea5b97c3ea0cdc.png', alt: 'Card 2' },
    { id: 3, src: '/imgCard/7a09640693403a43979b9a4739084f32d370db3c.png', alt: 'Card 3' },
    { id: 4, src: '/imgCard/917b42be1295ce1f172cb33877e54f1dec65e57f.png', alt: 'Card 4' },
    { id: 5, src: '/imgCard/a45850229f15ef811703228f465820f420082051.png', alt: 'Card 5' },
    { id: 6, src: '/imgCard/ba47b00bf8270ce37d877ac1a7145dc748420631.png', alt: 'Card 6' },
    { id: 7, src: '/imgCard/d2e3ad2a21dceaaeafc305c81f44cd9f06b16da3.png', alt: 'Card 7' }
  ];

  // Создаем бесконечный массив карточек
  const cards = [...originalCards, ...originalCards, ...originalCards];
  const cardSpacing = 200; // расстояние между карточками по диагонали
  const totalOffset = originalCards.length * cardSpacing;

  // Обработка мыши
  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    isDragging.current = true;
    startX.current = e.clientX;
    startOffset.current = offset;
    document.body.style.cursor = 'grabbing';
    e.preventDefault();
  }, [offset]);

  const handleMouseMove = useCallback((e: MouseEvent) => {
    if (!isDragging.current) return;
    
    const deltaX = e.clientX - startX.current;
    const newOffset = startOffset.current + deltaX;
    setOffset(newOffset);
  }, []);

  const handleMouseUp = useCallback(() => {
    isDragging.current = false;
    document.body.style.cursor = 'default';
  }, []);

  // Обработка колесика мыши
  const handleWheel = useCallback((e: React.WheelEvent) => {
    e.preventDefault();
    const delta = e.deltaY > 0 ? -50 : 50;
    setOffset(prev => prev + delta);
  }, []);

  // Бесконечная прокрутка
  useEffect(() => {
    if (offset > 0) {
      setOffset(offset - totalOffset);
    } else if (offset < -totalOffset) {
      setOffset(offset + totalOffset);
    }
  }, [offset, totalOffset]);

  // Добавляем глобальные обработчики мыши
  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [handleMouseMove, handleMouseUp]);

  return (
    <div 
      className="diagonal-slider-container"
      ref={sliderRef}
      onMouseDown={handleMouseDown}
      onWheel={handleWheel}
    >
      <div className="diagonal-slider-scene">
        {cards.map((card, index) => {
          // Расчет диагональной позиции от левого нижнего к правому верхнему углу
          const baseX = (index * cardSpacing) + offset;
          const baseY = -(index * cardSpacing * 0.6); // коэффициент наклона диагонали
          const baseZ = -(index * 30); // глубина для эффекта парения
          
          // Стартовая позиция - левый нижний угол экрана
          const startX = -600; // начинаем слева за экраном
          const startY = 300;  // начинаем снизу
          
          const diagonalX = baseX + startX;
          const diagonalY = baseY + startY;
          const diagonalZ = baseZ;
          
          // Поворот карточки боком
          const rotateY = -45; // поворачиваем карточки боком
          
          // Z-index для правильного наложения
          const zIndex = 1000 - index;
          
          // Убираем исчезновение карточек - они всегда видны
          const opacity = 1;
          
          return (
            <div
              key={`${card.id}-${Math.floor(index / originalCards.length)}`}
              className="diagonal-card"
              style={{
                transform: `translate3d(${diagonalX}px, ${diagonalY}px, ${diagonalZ}px) rotateY(${rotateY}deg)`,
                zIndex: zIndex,
                opacity: opacity
              }}
            >
              <img 
                src={card.src} 
                alt={card.alt}
                draggable={false}
              />
              <div className="card-shadow" />
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default DiagonalCardSlider;
