import React, { useState, useRef, useCallback } from 'react';
import './DiagonalCardSlider.css';

interface CardData {
  id: number;
  src: string;
  alt: string;
}

const DiagonalCardSlider: React.FC = () => {
  const [currentIndex, setCurrentIndex] = useState<number>(0);
  const sliderRef = useRef<HTMLDivElement>(null);
  
  // Список изображений
  const cards: CardData[] = [
    { id: 1, src: '/imgCard/4182c3a36ae0287e74f45e4bc4b0478087395003.png', alt: 'Card 1' },
    { id: 2, src: '/imgCard/59ba5cf45380257ee8e166783aea5b97c3ea0cdc.png', alt: 'Card 2' },
    { id: 3, src: '/imgCard/7a09640693403a43979b9a4739084f32d370db3c.png', alt: 'Card 3' },
    { id: 4, src: '/imgCard/917b42be1295ce1f172cb33877e54f1dec65e57f.png', alt: 'Card 4' },
    { id: 5, src: '/imgCard/a45850229f15ef811703228f465820f420082051.png', alt: 'Card 5' },
    { id: 6, src: '/imgCard/ba47b00bf8270ce37d877ac1a7145dc748420631.png', alt: 'Card 6' },
    { id: 7, src: '/imgCard/d2e3ad2a21dceaaeafc305c81f44cd9f06b16da3.png', alt: 'Card 7' }
  ];

  // Навигация
  const goToSlide = useCallback((index: number) => {
    setCurrentIndex(index);
  }, []);

  const nextSlide = useCallback(() => {
    setCurrentIndex((prev: number) => (prev + 1) % cards.length);
  }, [cards.length]);

  const prevSlide = useCallback(() => {
    setCurrentIndex((prev: number) => (prev - 1 + cards.length) % cards.length);
  }, [cards.length]);

  // Обработка touch событий
  const handleTouchStart = useRef<number | null>(null);
  
  const handleTouchStartEvent = (e: React.TouchEvent) => {
    handleTouchStart.current = e.touches[0].clientX;
  };

  const handleTouchEnd = (e: React.TouchEvent) => {
    if (!handleTouchStart.current) return;
    
    const touchEnd = e.changedTouches[0].clientX;
    const touchStart = handleTouchStart.current;
    
    if (touchStart - touchEnd > 50) {
      nextSlide();
    }
    
    if (touchEnd - touchStart > 50) {
      prevSlide();
    }
  };

  // Обработка клавиатуры
  const handleKeyDown = useCallback((e: React.KeyboardEvent) => {
    if (e.key === 'ArrowLeft') {
      prevSlide();
    } else if (e.key === 'ArrowRight') {
      nextSlide();
    }
  }, [nextSlide, prevSlide]);

  return (
    <div 
      className="diagonal-slider-container"
      ref={sliderRef}
      onTouchStart={handleTouchStartEvent}
      onTouchEnd={handleTouchEnd}
      onKeyDown={handleKeyDown}
      tabIndex={0}
    >
      <div className="diagonal-slider-scene">
        {cards.map((card, index) => {
          const offset = index - currentIndex;
          const isActive = index === currentIndex;
          
          // Расчет позиции по диагонали
          const diagonalOffset = offset * 200; // расстояние между карточками
          const xPos = diagonalOffset;
          const yPos = -diagonalOffset * 0.6; // коэффициент наклона диагонали
          const zPos = Math.abs(offset) * -150; // глубина
          
          // Расчет поворота
          const rotateX = offset * 15;
          const rotateY = offset * 25;
          const rotateZ = offset * 5;
          
          // Расчет масштаба
          const scale = isActive ? 1 : Math.max(0.4, 1 - Math.abs(offset) * 0.2);
          
          // Расчет прозрачности
          const opacity = Math.abs(offset) > 3 ? 0 : Math.max(0.3, 1 - Math.abs(offset) * 0.2);
          
          return (
            <div
              key={card.id}
              className={`diagonal-card ${isActive ? 'active' : ''}`}
              style={{
                transform: `
                  translate3d(${xPos}px, ${yPos}px, ${zPos}px)
                  rotateX(${rotateX}deg)
                  rotateY(${rotateY}deg)
                  rotateZ(${rotateZ}deg)
                  scale(${scale})
                `,
                opacity,
                zIndex: isActive ? 100 : Math.max(1, 50 - Math.abs(offset))
              }}
              onClick={() => !isActive && goToSlide(index)}
            >
              <div className="card-inner">
                <img 
                  src={card.src} 
                  alt={card.alt}
                  loading="lazy"
                />
                <div className="card-reflection" />
                <div className="card-shadow" />
              </div>
            </div>
          );
        })}
      </div>
      
      {/* Навигационные кнопки */}
      <button className="nav-btn prev" onClick={prevSlide} aria-label="Previous slide">
        &#8249;
      </button>
      <button className="nav-btn next" onClick={nextSlide} aria-label="Next slide">
        &#8250;
      </button>
      
      {/* Индикаторы */}
      <div className="indicators">
        {cards.map((_, index) => (
          <button
            key={index}
            className={`indicator ${index === currentIndex ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
      
      {/* Информация о текущем слайде */}
      <div className="slide-info">
        <span>{currentIndex + 1} / {cards.length}</span>
      </div>
    </div>
  );
};

export default DiagonalCardSlider;
