import React, { useState } from 'react';

const TestSlider: React.FC = () => {
  const [offset, setOffset] = useState<number>(0);

  const handleWheel = (e: React.WheelEvent) => {
    e.preventDefault();
    const delta = e.deltaY > 0 ? -50 : 50;
    setOffset(prev => prev + delta);
    console.log('Test wheel:', offset + delta);
  };

  return (
    <div 
      style={{ 
        width: '100vw', 
        height: '100vh', 
        background: '#333',
        position: 'relative',
        overflow: 'hidden'
      }}
      onWheel={handleWheel}
    >
      <div 
        style={{
          position: 'absolute',
          left: `${100 + offset}px`,
          top: '50%',
          width: '200px',
          height: '100px',
          background: 'red',
          color: 'white',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}
      >
        Offset: {offset}
      </div>
    </div>
  );
};

export default TestSlider;
